for(var modal=document.querySelectorAll(".modal"),modal_buttons=document.querySelectorAll("[data-modal]"),k=0;k<modal_buttons.length;k++)modal_buttons[k].addEventListener("click",function(e){e.preventDefault(),document.querySelector("."+this.dataset.modal).classList.add("modal-show")});for(var i=0;i<modal.length;i++)window.addEventListener("keydown",function(e){if(27===e.keyCode)for(var o=0;o<modal.length;o++)modal[o].classList.contains("modal-show")&&modal[o].classList.remove("modal-show")}),modal[i].querySelector(".modal-close").addEventListener("click",function(e){e.preventDefault(),this.parentNode.classList.contains("modal-show")&&this.parentNode.classList.remove("modal-show")}),document.querySelector(".btn.return")&&document.querySelector(".btn.return").addEventListener("click",function(e){e.preventDefault(),document.querySelector(".modal-cart").classList.contains("modal-show")&&document.querySelector(".modal-cart").classList.remove("modal-show")});
